ChinaMap.prototype = {
	draw : function(){
		var P = this.defProps;
		var R = Raphael(this.id, P.width, P.height);
		
		var _map = null, _attr = null;	
    	for( var i = 0, len = this.maps.length; i < len; ++i ){
    		_map = this.maps[i];
    		
    		if(!_map.path){
    			continue;	
    		}
    		
    		_attr = {
	            "fill": P.fillcolor,
	            "stroke": P.strokecolor,
	            "stroke-width": P.strokewidth,
	            "stroke-linejoin": "round"
	        };
    		
    		var rpath = R.path(_map.path).attr(_attr);
    		if( _map.coord && _map.name ){
    			var rtext = R.text(_map.coord[0], _map.coord[1], _map.name).attr({"font":P.font});
    			this.maps[i]["Rtext"] = rtext;	
    		}
  
    		this.maps[i]["Rpath"] = rpath;
    	}
    	
    	// 初始化一些鼠标事件：click,mouseover,mouseout
    	this._initEvent();
	},
	
	_initEvent : function(){
		var P = this.defProps;
		var hasClick = typeof P.click == 'function',
			hasOver = typeof P.mouseover == 'function',
		    hasOut = typeof P.mouseover == 'function';
		
		if( !hasOver && !hasOut && !hasClick ){
			return;
		} 
		
		var hoverTimer = null;
		var _this = this;    
		for( var i = 0, len = this.maps.length; i < len; ++i ){
	        (function(_map) {
	            var _path = _map['Rpath'], _Rtext = _map["Rtext"];
				
				var $path = $(_path[0]);
				if(hasClick){
					$path.css('cursor','pointer').bind("click", function(e){
						P.click.call(_path, e, _path, _map);
					});
					
					// 给文字也增加onclick事件，因为文字上的时间不会冒泡到path上
					if(_Rtext){
						$(_Rtext[0]).css('cursor','pointer').bind("click", function(e){
							P.click.call(_path, e, _path, _map);
						});
					}
				}
	            
	            if(hasOver){
	            	$path.bind("mouseover", function(e){
	            		if(hoverTimer){
		  					window.clearTimeout(hoverTimer);
		  					hoverTimer = null;
		  				}
		  				
		  				hoverTimer = window.setTimeout(function(){
	            			P.mouseover.call(_path, e, _path, _map);
	            		}, _this.defProps.delay);
	            	});
	            	
	            	// 给文字也增加onmouseover事件
					if(_Rtext){
						$(_Rtext[0]).bind("mouseover", function(e){
	            			if(hoverTimer){
			  					window.clearTimeout(hoverTimer);
			  					hoverTimer = null;
			  				}
			  				
			  				hoverTimer = window.setTimeout(function(){
		            			P.mouseover.call(_path, e, _path, _map);
		            		}, _this.defProps.delay);
	            		});
					}
	            }
	            
	            if(hasOut){
	            	$path.bind("mouseout", function(e){
	            		if(hoverTimer){
		  					window.clearTimeout(hoverTimer);
		  					hoverTimer = null;
		  				}
	            		P.mouseout.call(_path, e, _path, _map);
	            	});
	            	
	            	// 给文字也增加onmouseout事件
					if(_Rtext){
						$(_Rtext[0]).bind("mouseout", function(e){
							if(hoverTimer){
			  					window.clearTimeout(hoverTimer);
			  					hoverTimer = null;
			  				}
	            			P.mouseout.call(_path, e, _path, _map);
	            		});
					}
	            }
	            
	        })(this.maps[i]);
	    }
	},
	
	getMapData : function(){
		return this.maps;
	},
	
	// 重绘
	redraw : function(){}
};

